import React from "react";
import { useState } from "react";
import { Button, Form } from "react-bootstrap";
import { useSelector } from "react-redux";
import Header from "../../components/Header";
import Input from "../../components/Input";

const Profile = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const state = useSelector((state) => state);
  const auth = state.auth;
  console.log("auth",auth);

  const role = auth?.users?.role;

  return (
    <div>
      <Header />
      <div className="App Profile">
        <Form>
          <Input
            label="Full name"
            value={auth.users.name}
            onChange={(e) => setName(e.target.value)}
          />
          <Input
            label="Email"
            required={true}
            value={auth.users.email}
            onCange={(e) => setEmail(e.target.value)}
          />
          <label>Brief Profile</label>
          <textarea class="form-control" rows="4" id="Input" placeholder="Brief Profile"></textarea>

          {role === "student" ? (
             <Input className="Disable" placeholder="Signed in as Student" />

            ) : role === "company" ? (
              <Input className="Disable" placeholder="Signed in as Company" />

            ) : null}
          <Button className="Button" variant="primary">
            Save changes
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default Profile;
