import { POST_JOB, POST_JOB_DELETE } from "../Actions/types";
const initialState ={
    data:[]
}
const postJob = (state = initialState,action) => {
   switch(action.type){
    case POST_JOB:
        return{
         ...state,
         data: [
             ...state.data,
             {
                 message: action.payload.messsage
                }
         ]}
         case POST_JOB_DELETE:
            return{
             ...state,
            message: action.payload.messsage,
            index: action.playload.index        
             }      
        default:
        return state;

}
}
export default postJob;
