import { ALL_JOBS } from "../Actions/types";

const initialState = {
  jobs: [],
};
const allJob = (state = initialState, action) => {
  switch (action.type) {
    case ALL_JOBS:
      return {
        ...state,
        jobs: [
          ...state.jobs,
          {
            messsage: action.payload.messsage,
          },
        ],
      };
    default:
      return state;
  }
};
export default allJob;
