import { POST_JOB } from "../Actions/types";
const initialState ={
    message:[]
}
const postJob = (state = initialState,action) => {
   switch(action.type){
    case POST_JOB:
        return{
         ...state,
         message: action.payload.messsage
        }   
        default:
        return state;

}
}
export default postJob;
