import React, { useState } from "react";
import { useSelector } from "react-redux";
import Cards from "../../components/Cards";
import Header from "../../components/Header";
import { useDispatch } from "react-redux";
import postJobDelete from "../../Actions/postJobDelete";

const Vacancies = () => {
  const state = useSelector((state) => state);
  const postJobs = state.postJob.data;

  const [allJobs, setAllJobs] = useState(postJobs);

  // console.log("allJobs",allJobs);

  const dispatch = useDispatch();

  const handleDelete = (id) => {
    dispatch(postJobDelete(id));
  };

  return (
    <div className="App">
      <Header />
      <h1>VACANCIES HERE</h1>
      <div className="flex-container">
        {postJobs.map((data, index) => (
          <Cards
            title={data?.message?.title}
            description={data?.message?.description}
            gpa={`Minimum GPA Required : ${data?.message?.gpa}`}
            salary={`Tentative Salary : ${data?.message?.salary}`}
            button={`Delete`}
            footer={`Last date to apply : ${data?.message?.date}`}
            onClick={() => handleDelete(index)}
          />
        ))}
      </div>
    </div>
  );
};

export default Vacancies;
